import React, {useState} from 'react';
import './App.css';

function App() {
  const [quote, setQuote] = useState ("I didn’t fail the test. I just found 100 ways to do it wrong.");
  const [author, setAuthor] = useState ("Benjamin Franklin");
  const [randomNumber, setRandomNumber] = useState(0);
  const generaterandomnumber = () => {
    let randomInteger = Math.floor(3*Math.random());
    setRandomNumber(randomInteger)
  }

  const changeQuoteandAuthor0 = () => {
    setQuote = ("Strive not to be a success, but rather to be of value.");
    setAuthor = ("Albert Einstein")
  }

  const changeQuoteandAuthor1 = () => {
    setQuote = ("The most difficult thing is the decision to act, the rest is merely tenacity.");
    setAuthor = ("Amelia Earhart")
  }

  const changeQuoteandAuthor2 = () => {
    setQuote = ("Nothing is impossible, the word itself says, “I’m possible!”");
    setAuthor = ("Audrey Hepburn")
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>Random Number: {randomNumber}</h1>
        <button>Generate randomenumber</button>
       <p>
        "{quote}""
       </p>
       <p>
        - {author}
       </p> 
       <button onClick={()=> changeQuoteandAuthor0()}>New Quote 1 </button>
       <button onClick={()=> changeQuoteandAuthor1()}>New Quote 2</button>
        <button onClick={()=> changeQuoteandAuthor2()}>New Quote 3</button>
      </header>
    </div>
  );
}

export default App;
