import React, {useState} from 'react';
import './App.css';

function App() {
  const [quote, setQuote] = useState ("lll");
  let author = "Charles swindol";
  return (
    <div className="App">
      <header className="App-header">
       <p>
        {quote}
       </p>
       <p>
        - {author}
       </p>
       <button onClick={setQuote}>
        Change Quote
       </button>
      </header>
    </div>
  );
}

export default App;
