import React, {useState} from 'react';
import './App.css';

function App() {
  const [quote, setQuote] = useState ("I didn’t fail the test. I just found 100 ways to do it wrong.");
  const [author, setAuthor] = useState ("Benjamin Franklin");
  const [randomNumber, setRandomNumber] = useState(0);
  const generaterandomnumber = () => {
    let randomInteger = Math.floor(3*Math.random());
    setRandomNumber(randomInteger)
    if (randomInteger === 0){
      setQuote(quotesArray[0].quote)
      setAuthor(quotesArray[0].author)
    }
    if (randomInteger === 1){
      setQuote(quotesArray[1].quote)
      setAuthor(quotesArray[1].author)

    }
    if (randomInteger === 2){
      setQuote(quotesArray[2].quote)
      setAuthor(quotesArray[2].author)

    }
  }

  const quotesArray = [{quote: "Strive not to be a success, but rather to be of value.", author: "Albert Einstein"},{quote:"The most difficult thing is the decision to act, the rest is merely tenacity.", author: "Amelia Earhart"},{quote:"Nothing is impossible, the word itself says, “I’m possible!”", author:"Audrey Hepburn"}]

  return (
    <div className="App">
      <header className="App-header">
        <h1>Random Number: {randomNumber}</h1>
        <button onClick={()=> generaterandomnumber()}>Generate randomenumber</button>
       <p>
        "{quote}""
       </p>
       <p>
        - {author}
       </p>
      </header>
    </div>
  );
}

export default App;
